<?php $yourName='BW Geotextiles Web Form';$yourEmail='sales@bwgeotextiles.com';$yourSubject='New Web Lead';$referringPage='../';function cleanPosUrl($str){return stripslashes($str);}if(isset($_POST['sendContactEmail'])){$to=$yourEmail;$subject=$yourSubject.': '.$_POST['posRegard'];$message=cleanPosUrl($_POST['posText']);$headers="From: ".cleanPosUrl($_POST['posName'])." <".$_POST['posEmail'].">\r\n";$headers.='To: '.$yourName.' <'.$yourEmail.'>'."\r\n";$mailit=mail($to,$subject,$message,$headers);if(@$mailit){header('Location: '.$referringPage.'?success=true');}else{header('Location: '.$referringPage.'?error=true');}} ?>